import styled from 'styled-components';

export const HeaderContainer = styled.div`
    text-align: center;
    background-color: #C85248;
    border-radius: 25%;
    padding-top: 10px;
    padding-bottom: 20px;
    width: 400px;
    height: 60px;
    margin: auto;
    margin-top:50px;
`;

export const HeaderText = styled.div`
    position:flex;
    font-family: Superfats;
    font-size:40px;
    padding-top: 15px;
    padding-bottom: 15px;
    padding-left: 30px;
    padding-right: 30px;
`;

export const BodyText = styled.div`
    font-family: Baskerville;
    font-size:20px;
    padding-top: 20px;
    width: 800px;
    font-weight: bold;
`;
