import styled from 'styled-components';

export const HeaderContainer = styled.div`
    text-align: center;
    background-color: #C85248;
    border-radius: 25px;
    padding-top: 10px;
    padding-bottom: 20px;
    width: max(25%, 370px);
    height: 60px;
    margin: auto;
    margin-top:50px;
`;

export const HeaderText = styled.div`
    position:flex;
    font-family: Superfats;
    font-size: 250%;
    padding-top: 15px;
    padding-bottom: 15px;
    padding-left: 30px;
    padding-right: 30px;
`;

export const TextDiv = styled.div`
    text-align: center;
    margin-left: 20%;
    margin-right:20%;
    padding-bottom: max(1%, 20px);
    padding-top:max(1%, 20px);

`;

export const BodyText = styled.text`
    font-family: Baskerville;
    font-size:135%;
    font-weight: bold;
`;
